import { ABP, RoutesService } from '@abp/ng.core';
import { PRODUCT_BASE_ROUTES } from './product-base.routes';

export const PRODUCTS_PRODUCT_ROUTE_PROVIDER = [
  provideAppInitializer(() => {
    configureRoutes();
  }),
